//Load the destinations inside the departure and arrival sections inside the booking bar
function loadDestinations(){
    //Departure variable
    const departureSelect = document.getElementById('departure');
    //Arrival variable
    const arrivalSelect = document.getElementById('arrival');
    destinations.forEach(destination =>  {
        //User's option variable
        const optionDeparture = document.createElement('option');
        //This is how the possible options will look like after appending them dynamicly
        optionDeparture.value = destination.code;
        optionDeparture.textContent = `${destination.name} (${destination.code})`;
        departureSelect.appendChild(optionDeparture);
        //Getting the same values for our  arrival options just like for the departures
        const optionArrival = document.createElement('option');
        optionArrival.value = destination.code;
        optionArrival.textContent = `${destination.name} (${destination.code})`;
        //const arrivalOption = option.cloneNode(true);
        arrivalSelect.appendChild(optionArrival);
    });
}
//Call the function 
document.addEventListener("DOMContentLoaded",loadDestinations);

//Ticket type and pricing cards based on the trip type
document.addEventListener("DOMContentLoaded", function () {
    const searchButton = document.querySelector('.btn.btn-primary');
    const economyCheckBox = document.querySelector('#economy');
    const buisnessCheckBox = document.querySelector("#buisness1");
    const pricingCards = document.querySelectorAll('.pricing-card');
    const checkboxContainer = document.querySelectorAll('.form-check');
    const passengers = document.getElementById('passengers');
    const cancel = document.querySelector('#cancel');
    const label1 = document.querySelector('#form-check-economy');
    const label2 = document.querySelector('#form-check-buisness');
    let countOfPassengers = 1;
    //Prices for our cards
    function randomPrices (min,max) {
        return (Math.random() * (max-min) + min).toFixed(2);
    }
    //If the number of passengers, then multiply the numbers by the number of passengers
    passengers.addEventListener('change', function () {
        //Transform the id into a integer
        countOfPassengers = parseInt(passengers.value);
        uploadPrices();
    });
    function uploadPrices () {
    const economyBasic = (parseFloat(randomPrices(37,60)) * countOfPassengers); 
    const economyFlex = (parseFloat(randomPrices(61,89)) * countOfPassengers) ; 
    const economyComfort = (parseFloat(randomPrices(90,150)) * countOfPassengers); 
    const buisnessPrice = (parseFloat(randomPrices(151,450)) * countOfPassengers);
    //Load the price values in pricing cards
    document.querySelector('#basic .price').textContent = `€${economyBasic}`;
    document.querySelector('#flex .price').textContent = `€${economyFlex}`;
    document.querySelector('#comfort .price').textContent = `€${economyComfort}`;
    document.querySelector('#buisness .price').textContent = `€${buisnessPrice}`;
    };
    uploadPrices();

    pricingCards.forEach(card => {
        card.style.display ='none';
    });
    checkboxContainer.forEach(container => {
        container.style.display = 'none';
    });
    //If search button is pressed, then show the ticket type check boxes
    searchButton.addEventListener('click',function () {
        checkboxContainer.forEach(container => {
            container.style.display = 'block';
        });
        //Show the economy class tickets when Economy checkbox is checked and hide the buisness one
        if (economyCheckBox.checked) {
            document.getElementById('basic').style.display = 'block';
            document.getElementById('flex').style.display = 'block';
            document.getElementById('comfort').style.display = 'block';
            document.getElementById('buisness').style.display = 'none';
            cancel.style.display = 'block';
        } else if (buisnessCheckBox.checked){
            //Do the opposite
            document.getElementById('basic').style.display = 'none';
            document.getElementById('flex').style.display = 'none';
            document.getElementById('comfort').style.display = 'none';
            document.getElementById('buisness').style.display = 'block';
            cancel.style.display = 'block';
        }
    });
    //If the checkboxes change condition, then re-do what you did
    economyCheckBox.addEventListener('change', function () {
        if (economyCheckBox.checked) {
            buisnessCheckBox.checked = false;
            document.getElementById('basic').style.display = 'block';
            document.getElementById('flex').style.display = 'block';
            document.getElementById('comfort').style.display = 'block';
            document.getElementById('buisness').style.display = 'none';
            cancel.style.display = 'block';
        } 
    });
    buisnessCheckBox.addEventListener('change',function () {
        if (buisnessCheckBox.checked){
            economyCheckBox.checked = false;
            document.getElementById('basic').style.display = 'none';
            document.getElementById('flex').style.display = 'none';
            document.getElementById('comfort').style.display = 'none';
            document.getElementById('buisness').style.display = 'block';
            cancel.style.display = 'block';
        }
    });
    cancel.addEventListener('click',function () {
        economyCheckBox.style.display = 'none';
        buisnessCheckBox.style.display = 'none';
        document.getElementById('basic').style.display = 'none';
        document.getElementById('flex').style.display = 'none';
        document.getElementById('comfort').style.display = 'none';
        document.getElementById('buisness').style.display = 'none';
        cancel.style.display = 'none';
        label1.style.display = 'none';
        label2.style.display = 'none';
    });
});